mixed-library.configContent:
import { defineConfig, LibraryType } from '@ldesign/builder'

export default defineConfig({
  // 多入口配�?- 自动扫描 src 目录下的所�?TypeScript 文件
  input: ['src/**/*.ts'],

  // 输出配置
  output: {
    format: ['esm'], // Rolldown 目前主要支持 ESM
    sourcemap: true,
    name: 'MixedLibrary' // UMD格式需要全局变量�?
  },

  // 库类�?- 混合类型�?
  libraryType: LibraryType.MIXED,

  // 打包器选择
  bundler: 'rolldown',

  // UMD 构建配置 - Rolldown 暂不支持
  umd: {
    enabled: false, // 禁用 UMD 以兼容 Rolldown
    entry: 'src/index.ts',
    name: 'MixedLibrary',
    forceMultiEntry: false,
    minify: false
  },

  // Babel 转换配置
  babel: {
    enabled: false, // 可以启用 Babel 转换
    targets: '> 1%, last 2 versions, not dead'
  },

  // Banner �?Footer 配置
  banner: {
    banner: '/*! Mixed Library - A comprehensive library with TypeScript and styles */',
    copyright: {
      owner: 'LDesign Team',
      year: 2024,
      license: 'MIT'
    },
    buildInfo: {
      version: true,
      buildTime: true,
      environment: true
    }
  },

  // TypeScript 配置
  typescript: {
    declaration: true,
    declarationDir: 'dist',
    target: 'ES2020',
    module: 'ESNext',
    strict: true,
    skipLibCheck: true
  },

  // 样式配置 - Rolldown 暂不支持 LESS
  style: {
    extract: false,       // 禁用 CSS 提取以兼容 Rolldown
    minimize: false,
    autoprefixer: false,  // 禁用自动前缀
    modules: false,
    preprocessor: {
      less: {
        enabled: false,   // 禁用 LESS 处理
        options: {
          javascriptEnabled: true
        }
      },
      sass: {
        enabled: false
      }
    }
  },

  // 外部依赖（不打包到输出中�?  external: [],

  // 性能配置
  performance: {
    treeshaking: true,
    minify: false, // 默认不压�?    bundleAnalyzer: false
  },

  // 默认不压�?  minify: false,

  // 清理输出目录
  clean: true,

  // 日志级别
  logLevel: 'info'
})

