react-components.configContent:
import { defineConfig } from '@ldesign/builder'

export default defineConfig({
  // 多入口配�?- 自动扫描 src 目录下的所�?TypeScript �?React 文件
  input: ['src/**/*.ts', 'src/**/*.tsx'],

  // 输出配置
  output: {
    format: ['esm', 'cjs', 'umd'],
    sourcemap: true,
    name: 'ReactComponents', // UMD格式需要全局变量�?
    globals: {
      react: 'React',
      'react-dom': 'ReactDOM'
    }
  },

  // 库类�?- React 组件�?
  libraryType: 'react', // 使用 React 策略

  // 打包器选择
  bundler: 'rollup',

  // React 配置
  react: {
    jsx: {
      enabled: true,
      pragma: 'React.createElement',
      pragmaFrag: 'React.Fragment',
      runtime: 'classic' // �?'automatic'
    }
  },

  // TypeScript 配置
  typescript: {
    declaration: true,
    declarationDir: 'dist',
    target: 'ES2020',
    module: 'ESNext',
    strict: true,
    skipLibCheck: true,
    jsx: 'react-jsx' // �?'react'
  },

  // 样式配置
  style: {
    extract: true,        // 提取 CSS 到单独文�?
    minimize: true,       // 压缩 CSS
    autoprefixer: true,   // 自动添加浏览器前缀
    modules: false,       // 不使�?CSS Modules
    preprocessor: {
      less: {
        enabled: false
      },
      sass: {
        enabled: false
      }
    }
  },

  // 外部依赖（不打包到输出中�?
  external: ['react', 'react-dom', 'react/jsx-runtime'],



  // 性能配置
  performance: {
    treeshaking: true,
    minify: true,
    bundleAnalyzer: false
  },

  // 清理输出目录
  clean: true,

  // 日志级别
  logLevel: 'info'
})

style-library.configContent:
import { defineConfig, LibraryType } from '@ldesign/builder'

export default defineConfig({
  // 样式库入口 - 为兼容 Rolldown 使用 TypeScript 入口
  input: 'src/index.ts',

  // 输出配置 - 样式库主要输�?CSS，但需�?ESM 格式来处理样式导�?
  output: {
    format: ['esm'],
    sourcemap: true
  },

  // 库类�?- 样式�?
  libraryType: LibraryType.STYLE,

  // 打包器选择
  bundler: 'rolldown',

  // 样式配置 - Rolldown 兼容模式
  style: {
    extract: false,       // 禁用 CSS 提取以兼容 Rolldown
    minimize: false,      // 禁用 CSS 压缩
    autoprefixer: false,  // 禁用自动前缀
    modules: false,
    preprocessor: {
      less: {
        enabled: false,   // 禁用 LESS 处理
        options: {
          javascriptEnabled: true,
          modifyVars: {}
        }
      },
      sass: {
        enabled: false
      }
    },
    browserslist: [
      '> 1%',
      'last 2 versions',
      'not dead',
      'not ie 11'
    ]
  },

  // 外部依赖（样式库通常不需要外部依赖）
  external: [],

  // 性能配置
  performance: {
    treeshaking: false,   // CSS 不需�?Tree Shaking
    minify: true,         // 压缩输出
    bundleAnalyzer: false
  },

  // 清理输出目录
  clean: true,

  // 日志级别
  logLevel: 'info'
})

